import axios from "axios";

const ApiConnection = axios.create({ baseURL: "http://localhost:5001/api" });

// Route for Task Creation
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import TaskCreation from "./pages/TaskCreation";

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/tasks/new" element={<TaskCreation />} />
      </Routes>
    </Router>
  );
}

//CRUD API handling, will be imported into each other file
export const DisTasks = async () => await ApiConnection.get("/tasks");
export const CreateTask = async (create) =>
  await ApiConnection.post("/tasks", create);
export const EditTask = async (id, edit) =>
  await ApiConnection.put(`/tasks/${id}`, edit);
export const DelTask = async (id) => await ApiConnection.delete(`/tasks/${id}`);
export const TaskAn = async () => await ApiConnection.get("/tasks/analytics");
export default ApiConnection;
